import React, { useEffect, useState } from "react";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import { useHttpClient } from "../../shared/hooks/http-hook";
import ErrorModal from "../../shared/components/UIElements/ErrorModal";
import LoadingSpinner from "../../shared/components/UIElements/LoadingSpinner";
import { VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hooks";

import "./Answer.css";

const Answer = () => {
  const { isLoading, error, sendRequest, clearError } = useHttpClient();
  const [loadedSubmissionResult, setLoadedSubmissionResult] = useState("");

  const codeString =
    "public class Solution {public static void main(String[] args) {System.out.println(-321);} }";

  const [formState, inputHandler] = useForm(
    {
      answer: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  useEffect(() => {
    setLoadedSubmissionResult("shashliki");
    console.log(loadedSubmissionResult);
  }, []);

  const submitQuestion = async () => {
    try {
      const answerSubmissionResponse = await sendRequest(
        "http://51.138.73.135:8080/Algors/submit",
        "POST",
        JSON.stringify({
          answer: codeString,
          language: "java",
          version: "0",
          courseId: "1",
          userId: "1",
          questionId: "1",
        }),
        {}
      );
      console.log(answerSubmissionResponse);
      setLoadedSubmissionResult(answerSubmissionResponse);
    } catch (err) {}
  };

  const authSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  // const [code, setCode] = React.useState(
  //   "public class Solution {public static void main(String[] args) {System.out.println(-321);} }"
  // );

  return (
    <form onSubmit={authSubmitHandler}>
      <Input
        className="codeare"
        id="answer"
        element="codearea"
        label="Your answer:"
        validators={[VALIDATOR_MINLENGTH(3)]}
        errorText="Please enter a valid code answer"
        placeholder="test placeholder"
        onInput={inputHandler}
        initialValue={codeString}
        language="js"
        value={codeString}
      />
      <ErrorModal error={error} onClear={clearError} />
      {isLoading && (
        <div className="center">
          <LoadingSpinner />
        </div>
      )}
      {!isLoading && (
        <Input
          id="result"
          element="codearea"
          label="Submit Result"
          onInput={inputHandler}
          language="js"
          value={loadedSubmissionResult}
        />
      )}
      <Button
        type="submit"
        onClick={submitQuestion}
        disabled={formState.isValid}
      >
        Submit
      </Button>
    </form>
  );
};

export default Answer;
