import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";

import Card from "../../shared/components/UIElements/Card";
import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import ErrorModal from "../../shared/components/UIElements/ErrorModal";
import LoadingSpinner from "../../shared/components/UIElements/LoadingSpinner";

import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
  VALIDATOR_REQUIRE,
} from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hooks";
import { AuthContext } from "../../shared/context/auth-context";
import "./Auth.css";

const Auth = () => {
  const auth = useContext(AuthContext);
  const [isLoginMode, setIsLoginMode] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState();

  const [formState, inputHandler, setFormData] = useForm(
    {
      name: {
        value: "",
        isValid: false,
      },
      email: {
        value: "",
        isValid: false,
      },
      password: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const switchModeHandler = () => {
    if (!isLoginMode) {
      setFormData(
        {
          ...formState.inputs,
          name: undefined,
        },
        formState.inputs.email.isValid && formState.inputs.password.isValid
      );
    } else {
      setFormData(
        {
          ...formState.inputs,
          name: {
            value: "",
            isValid: false,
          },
        },
        false
      );
    }
    setIsLoginMode((prevMode) => !prevMode);
  };

  const authSubmitHandler = (event) => {
    event.preventDefault();

    if (isLoginMode) {
    } else {
      setIsLoading(true);
      fetch("http://51.138.73.135:8080/Algors/signUp", {
        method: "POST",
        headers: {},
        body: JSON.stringify({
          email: formState.inputs.email.value,
          userName: formState.inputs.name.value,
          password: formState.inputs.password.value,
        }),
      })
        .then((response) => { 
          console.log(response);

          response.json().then((json) => {
            console.log(json);
            if(!json.ok) {
              throw new Error(json.message);
            }
            if(json.id == -1) {
              throw new Error("User already exists.");
            }
            setIsLoading(false);
            auth.login();
          });
        })
        .catch((err) => {
          console.log(err);
          setIsLoading(false);
          setError(err.message || "Something went wrong, please try again.");
        });
    }
    console.log(formState.inputs);
  };

  return (
    <Card className="authentication">
      <h2>Just a moment and you're in</h2>
      <form onSubmit={authSubmitHandler}>
        {!isLoginMode && (
          <Input
            element="input"
            id="name"
            type="text"
            placeholder="name"
            validators={[VALIDATOR_REQUIRE()]}
            errorText="Please enter your name."
            onInput={inputHandler}
          />
        )}
        <Input
          element="input"
          id="email"
          type="email"
          placeholder="email"
          validators={[VALIDATOR_EMAIL()]}
          errorText="Please enter a valid email address."
          onInput={inputHandler}
        />
        <Input
          element="input"
          id="password"
          type="password"
          placeholder="password"
          validators={[VALIDATOR_MINLENGTH(5)]}
          errorText="Please enter a valid password, at least 5 characters."
          onInput={inputHandler}
        />
        <div className="authentication__links">
          <Link className="authentication__link">forgot password?</Link>
          <Link className="authentication__link">new bro? sign up.</Link>
        </div>
        <Button type="submit" disabled={!formState.isValid}>
          {isLoginMode ? "Sign in" : "Create acount"}
        </Button>
      </form>

      {/* <Button inverse onClick={switchModeHandler}>
        SWITCH TO {isLoginMode ? "SIGNUP" : "LOGIN"}
      </Button> */}
    </Card>
  );
};

export default Auth;
