import React, { useState } from "react";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import { VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hooks";

import "./Answer.css";

const Answer = () => {
  const codeString =
    "public class Solution {\r\n\tpublic static void main(String[] args){\r\n\t\tSystem.out.println(-321);\r\n\t}\r\n}";

  const [formState, inputHandler] = useForm(
    {
      answer: {
        value: "",
        isValid: false,
      },
    },
    false
  );

  const authSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  const [code, setCode] = React.useState(
    "public class Solution {public static void main(String[] args) {System.out.println(-321);} }"
  );

  return (
    <form onSubmit={authSubmitHandler}>
      <Input
        id="answer"
        element="codearea"
        label="Your answer:"
        validators={[VALIDATOR_MINLENGTH(3)]}
        errorText="Please enter a valid code answer"
        placeholder="test placeholder"
        onInput={inputHandler}
        initialValue={codeString}
        language="js"
        value={codeString}
      />
      <Button type="submit" disabled={!formState.isValid}>
        Submit
      </Button>
    </form>
  );
};

export default Answer;
