import React, { useEffect, useState } from "react";
import ScrollArea from "react-scrollbar";

import CoursesList from "../components/CoursesList";

import "./Courses.css";

const Courses = () => {
  const [loadedAllCourses, setLoadedAllCourses] = useState([]);
  const [isLoadingAllCourses, setIsLoadingAllCourses] = useState(false);

  const [loadedUserCourses, setLoadedUserCourses] = useState([]);
  const [isLoadingUserCourses, setIsLoadingUserCourses] = useState(false);

  useEffect(() => {
    document.body.style.background =
      "linear-gradient(180deg, #FFFFFF 50%, rgba(226, 211, 175, 0.25) 50%)";
    document.body.style.backgroundRepeat = "no-repeat";
    document.body.style.height = "100%";
    document.body.style.margin = "0";
    document.body.style.backgroundAttachment = "fixed";

    const fetchAllCourses = async () => {
      setIsLoadingAllCourses(true);
      fetch("http://51.138.73.135:8080/Algors/allCourses", {
        method: "GET",
        headers: {},
      })
        .then((res) => {
          console.log(res);
          res.json().then((json) => {
            console.log(json);
            setLoadedAllCourses(json.courses);
          });
        })
        .catch((err) => console.log(err));

      setIsLoadingAllCourses(false);
    };

    const fetchUserCourses = async () => {
      setIsLoadingUserCourses(true);
      fetch("http://51.138.73.135:8080/Algors/userCourses", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
        },
        body: JSON.stringify({ id: "1" }),
      })
        .then((res) => {
          console.log(res);
          res.json().then((json) => {
            console.log(json);
            setLoadedUserCourses(json.courses);
          });
        })
        .catch((err) => console.log(err));

      setIsLoadingUserCourses(false);
    };
    fetchUserCourses();
    fetchAllCourses();
  }, []);

  const MY_COURSES = [
    {
      id: "u1",
      name: "Begginer 1",
      image:
        "https://raw.githubusercontent.com/voodootikigod/logo.js/master/js.png",
    },
    {
      id: "u2",
      name: "Begginer 2",
      image:
        "data:image/png;base64,",
    },
  ];

  const TEST_COURSES = {
    courses: {
      1: {
        courseName: "Beginners A",
        subjects: "while, hash map",
        courseId: "1",
      },
    },
  };

  return (
    <React.Fragment className="course">
      {isLoadingUserCourses && <p className="loader">Loading...</p>}
      {!isLoadingAllCourses && (
        <CoursesList items={loadedUserCourses} className="course-list__list" />
      )}
      {isLoadingAllCourses && <p className="loader">Loading...</p>}
      {!isLoadingAllCourses && (
        <CoursesList
          blalba="shotota"
          items={loadedAllCourses}
          className="course-list__list"
        />
      )}
    </React.Fragment>
  );
};

export default Courses;
