import React, { useEffect, useState } from "react";
import ScrollArea from "react-scrollbar";

import { useHttpClient } from "../../shared/hooks/http-hook";
import CoursesList from "../components/CoursesList";
import LoadingSpinner from "../../shared/components/UIElements/LoadingSpinner";

import "./Courses.css";

const Courses = () => {
  const { isLoading, error, sendRequests, clearError } = useHttpClient();
  const [loadedAllCourses, setLoadedAllCourses] = useState([]);
  const [isLoadingAllCourses, setIsLoadingAllCourses] = useState(false);

  const [loadedUserCourses, setLoadedUserCourses] = useState([]);
  const [isLoadingUserCourses, setIsLoadingUserCourses] = useState(false);

  useEffect(() => {
    document.body.style.background =
      "linear-gradient(180deg, #FFFFFF 50%, rgba(226, 211, 175, 0.25) 50%)";
    document.body.style.backgroundRepeat = "no-repeat";
    document.body.style.height = "100%";
    document.body.style.margin = "0";
    document.body.style.backgroundAttachment = "fixed";

    const fetchAllCourses = async () => {
      try {
        const allCoursesResponse = await sendRequests(
          "http://51.138.73.135:8080/Algors/allCourses"
        );
        setLoadedAllCourses(allCoursesResponse);
      } catch (err) {}
    };

    const fetchUserCourses = async () => {
      try {
        const userCoursesResponse = await sendRequests(
          "http://51.138.73.135:8080/Algors/userCourses",
          "POST",
          JSON.stringify({
            userId: "1",
          }),
          {}
        );
        setLoadedUserCourses(userCoursesResponse);
      } catch (err) {}
    };

    fetchUserCourses();
    fetchAllCourses();
  }, [sendRequests]);

  const MY_COURSES = [
    {
      id: "u1",
      name: "Begginer 1",
      image:
        "https://raw.githubusercontent.com/voodootikigod/logo.js/master/js.png",
    },
    {
      id: "u2",
      name: "Begginer 2",
      image:
        "data:image/png;base64,",
    },
  ];

  const TEST_COURSES = {
    courses: {
      1: {
        courseName: "Beginners A",
        subjects: "while, hash map",
        courseId: "1",
      },
    },
  };

  return (
    <React.Fragment className="course">
      {(isLoadingUserCourses || isLoadingAllCourses) && (
        <div className="center">
          <LoadingSpinner />
        </div>
      )}
      {!isLoadingAllCourses && (
        <CoursesList items={loadedUserCourses} className="course-list__list" />
      )}
      {!isLoadingAllCourses && (
        <CoursesList items={loadedAllCourses} className="course-list__list" />
      )}
    </React.Fragment>
  );
};

export default Courses;
